mapper.insert:

    SELECT LAST_INSERT_ID() as board_id


INSERT INTO ev_board
(
    board_id
    , board_res_id
    , board_clsf_cd
    , board_clsf_dtl_cd
    , reg_dt
    , board_subject
    , board_content
    , board_content2
    , ref_url
    , evnt_pticpt
    , evnt_target
    , evnt_place
    , evnt_fr_dt
    , evnt_to_dt
    , req_fr_dt
    , req_fr_hhmm
    , req_to_dt
    , req_to_hhmm
    , use_yn
    , del_yn
    , board_stat_cd
    , reg_user_id
    , conslt_cust_id
    , conslt_user_id
    , board_cnt
    , ins_user
    , ins_dtm
    , mod_user
    , mod_dtm
) VALUES (
    NULL
    , nvl(#{board_res_id},0) /* 최초등록 0, 답변 : board_id */
    , #{board_clsf_cd}
    , #{board_clsf_dtl_cd}
    , nvl(#{reg_dt}, CURRENT_DATE())
    , #{board_subject}
    , #{board_content}
    , #{board_content2}
    , #{ref_url}
    , #{evnt_pticpt}
    , #{evnt_target}
    , #{evnt_place}
    , #{evnt_fr_dt}
    , #{evnt_to_dt}
    , #{req_fr_dt}
    , #{req_fr_hhmm}
    , #{req_to_dt}
    , #{req_to_hhmm}
    , 'Y'
    , 'N'
    , #{board_stat_cd}
    , #{reg_user_id}
    , #{conslt_cust_id}
    , #{conslt_user_id}
    , 0
    , #{user_id}
    , NOW()
    , #{user_id}
    , NOW()
)

mapper.update:
UPDATE ev_board
    SET board_subject = #{board_subject}
      , board_content = #{board_content}
      , ref_url       = #{ref_url}
      , evnt_pticpt   = #{evnt_pticpt}
      , evnt_target   = #{evnt_target}
      , evnt_place    = #{evnt_place}
      , evnt_fr_dt    = #{evnt_fr_dt}
      , evnt_to_dt    = #{evnt_to_dt}
      , req_fr_dt     = #{req_fr_dt}
      , req_fr_hhmm   = #{req_fr_hhmm}
      , req_to_dt     = #{req_to_dt}
      , req_to_hhmm   = #{req_to_hhmm}
      , conslt_user_id= #{conslt_user_id}
      , use_yn        = nvl(#{use_yn}, use_yn)
      , mod_user      = #{user_id}
      , mod_dtm       = NOW()
WHERE board_id = #{board_id}

UPDATE ev_board
   SET del_yn       = 'Y'
     , mod_user     = #{user_id}
     , mod_dtm      = NOW()
 WHERE board_id = #{board_id}

UPDATE ev_board
SET mod_user          = #{user_id}
  , mod_dtm           = NOW()
  , board_content   = #{board_content}
  , board_stat_cd   = #{board_stat_cd}
WHERE board_id = #{board_id}

UPDATE ev_board
SET mod_user      = #{user_id}
, mod_dtm         = NOW()
, board_stat_cd   = #{board_stat_cd}
WHERE board_id = #{board_id}

UPDATE ev_board
   SET board_cnt    = board_cnt + 1
 WHERE board_id     = #{board_id}

mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY a.ins_dtm DESC) as rn , a.board_id , a.board_subject , a.conslt_cust_id , concat(nvl((select concat(cd_nm, '>') from ev_comm_code ecc where cd_id = b.category1), ''), (select cust_nm from ev_cust_mst where cust_id = a.conslt_cust_id)) as conslt_cust_nm , a.reg_user_id , (select cust_nm from ev_cust_mst where cust_id = a.reg_user_id) as reg_user_nm , a.reg_dt , a.board_stat_cd , (select cd_nm from ev_comm_code where cd_id = a.board_stat_cd) as board_stat_nm FROM ev_board a inner join ev_cust_mst b on a.conslt_cust_id = b.cust_id WHERE a.board_clsf_cd = '101002' /* 1:1 문의 */ AND a.del_yn = 'N' AND a.board_res_id = 0
/* 참관로그인 */ AND a.reg_user_id = #{reg_user_id}
/* 참가로그인 - 질문 또는 답변 가능 */ AND (a.conslt_cust_id = #{conslt_cust_id} or a.reg_user_id = #{conslt_cust_id})
and board_subject like concat('%',#{keyword},'%')
and board_content like concat('%',#{keyword},'%')
and (board_subject like concat('%',#{keyword},'%') or board_content like concat('%',#{keyword},'%'))
and board_stat_cd = #{board_stat_cd}
LIMIT #{page_row_start}, #{page_row_cnt}

SELECT COUNT(*) AS row_count
FROM ev_board a
WHERE a.board_clsf_cd = '101002' /* 1:1 문의 */
AND a.del_yn = 'N'
AND a.board_res_id = 0


    /* 참관로그인 */
    AND a.reg_user_id = #{reg_user_id}



    /* 참가로그인 */
    AND a.conslt_cust_id = #{conslt_cust_id}



    and board_subject like concat('%',#{keyword},'%')



    and board_content like concat('%',#{keyword},'%')



    and (board_subject like concat('%',#{keyword},'%') or board_content like concat('%',#{keyword},'%'))



    and board_stat_cd = #{board_stat_cd}

select COUNT(*) AS row_count
from ev_board eb
where board_clsf_cd = #{board_clsf_cd}
LIMIT #{page_row_start} ,#{page_row_cnt}

select ROW_NUMBER() OVER(ORDER BY eb.ins_dtm DESC) as rn , board_id, board_res_id, board_clsf_cd, board_clsf_dtl_cd, reg_dt, board_subject , board_content, board_content2, ref_url, evnt_pticpt, evnt_target, evnt_place , evnt_fr_dt, evnt_to_dt, concat(evnt_fr_dt, '-', evnt_to_dt) as evnt_prod_dt , req_fr_dt, req_fr_hhmm , req_to_dt, req_to_hhmm, use_yn , del_yn, board_stat_cd, reg_user_id, conslt_cust_id, conslt_user_id, nvl(board_cnt,0) as board_cnt , (select mgnt_nm from ev_mgnt_user where mgnt_id = eb.ins_user) as ins_user , date_format(ins_dtm,'%Y-%m-%d %H:%i:%S') as ins_dtm from ev_board eb where board_clsf_cd = #{board_clsf_cd} and del_yn = 'N'
and eb.board_clsf_dtl_cd = #{board_clsf_dtl_cd}
and eb.evnt_fr_dt >= #{ins_dt_fr}
and eb.evnt_to_dt <= #{ins_dt_to}
and eb.board_subject like concat('%', #{keyword}, '%')
LIMIT #{page_row_start}, #{page_row_cnt}
