mapper.insert:

    select nvl(MAX(board_seq), 0) + 1 AS board_seq from ev_file_attach where board_id = #{board_id}


INSERT INTO ev_file_attach
(
    attach_id
    , board_id
    , board_seq
    , file_clsf_cd
    , file_clsf_dtl_cd
    , file_name
    , org_file_name
    , file_size
    , file_path
    , ins_user
    , ins_dtm
) VALUES (
    NULL
    , #{board_id}
    , #{board_seq}
    , #{file_clsf_cd}
    , #{file_clsf_dtl_cd}
    , #{file_name}
    , #{org_file_name}
    , #{file_size}
    , #{file_path}
    , #{user_id}
    , NOW()
)

mapper.select:
/* 첨부파일조회 */
SELECT attach_id
    , board_id
    , board_seq
    , file_clsf_cd
    , file_clsf_dtl_cd
    , file_name
    , org_file_name
    , file_size
    , file_path
FROM ev_file_attach
WHERE board_id = #{board_id}


    and board_seq = #{board_seq}



    and file_clsf_cd = #{file_clsf_cd}



    and file_clsf_cd = #{file_clsf_cd}


ORDER BY file_clsf_cd, file_clsf_dtl_cd, file_name
