mapper.select:
/* 회원중복체크 */
SELECT COUNT(*) AS CNT
  FROM ev_cust_mst
 where cust_id = #{cust_id}

/* 로그인 회원정보 조회 */
SELECT cust_seq
     , cust_id as cust_id
     , cust_id as user_id
     , cust_nm
     , email_id
     , cust_clsf_cd
FROM ev_cust_mst
where cust_id = #{user_id}
  and cust_pw = #{user_pw}
  /* and cust_sts_cd = '105001' --회원구분은 일단 제외 */

/* 로그인 회원정보 조회 */
SELECT cust_seq
     , cust_id as cust_id
     , cust_id as user_id
     , cust_nm
     , email_id
     , cust_clsf_cd
FROM ev_cust_mst
where 1=1


            AND google_email = #{google_email}


            AND naver_email = #{naver_email}


            AND kakao_email = #{kakao_email}


            AND facebook_email = #{facebook_email}

/* 로그인 회원정보 조회 */
SELECT cust_seq
, cust_id as cust_id
, cust_id as user_id
, cust_nm
, email_id
, cust_clsf_cd
FROM ev_cust_mst
where cust_nm = #{cust_nm}
  and email_id = #{email_id}

/* 로그인 회원정보 조회 */
SELECT cust_seq
     , cust_id as cust_id
     , cust_id as user_id
     , cust_nm
     , email_id
     , cust_clsf_cd
FROM ev_cust_mst
where cust_id = #{cust_id}
  and cust_nm = #{cust_nm}
  and email_id = #{email_id}

mapper.insert:

    SELECT LAST_INSERT_ID() as cust_seq


/* 회원등록 */
INSERT INTO ev_cust_mst
(
      cust_seq, cust_id, cust_pw, cust_clsf_cd, cust_nm
    , company_nm, dept_nm, rep_nm, posn_nm, gender_cd
    , age_cd, hp_no, tel_no, email_id, country_cd
    , zip_code, addr_1, addr_2
    , city_nm, visit_pups_cd, rcmder_cust_id, agree_1, agree_dtm_1
    , agree_2, agree_dtm_2, agree_3, agree_dtm_3, ins_user
    , ins_dtm, mod_user, mod_dtm, google_email, naver_email, kakao_email, facebook_email, reg_provider
) VALUES (
      NULL, #{cust_id}, #{cust_pw}, #{cust_clsf_cd}, #{cust_nm}
    , #{company_nm}, #{dept_nm}, #{rep_nm}, #{posn_nm}, #{gender_cd}
    , #{age_cd}, #{hp_no}, #{tel_no}, #{email_id}, #{country_cd}
    , #{zip_code}, #{addr_1}, #{addr_2}
    , #{city_nm}, #{visit_pups_cd}, #{rcmder_cust_id}, #{agree_1}, NOW()
    , #{agree_2}, NOW(), #{agree_3}, NOW(), #{user_id}
    , NOW(), #{user_id}, NOW(), #{google_email}, #{naver_email}, #{kakao_email}, #{facebook_email}, #{reg_provider}
)

/* 회원관심사항 입력 */
INSERT INTO ev_cust_intrst
(
    intrst_id, cust_id, intrst_cd, del_yn, ins_user, ins_dtm, mod_user, mod_dtm
) VALUES (
    NULL, #{cust_id}, #{intrst_cd}, 'N', #{user_id}, NOW(), #{user_id}, NOW()
)

mapper.update:
/* 회원정보수정 */
UPDATE ev_cust_mst
   SET company_nm       = #{company_nm}
     , dept_nm          = #{dept_nm}
     , rep_nm           = #{rep_nm}
     , posn_nm          = #{posn_nm}
     , gender_cd        = #{gender_cd}
     , age_cd           = #{age_cd}
     , hp_no            = #{hp_no}
     , tel_no           = #{tel_no}
     , email_id         = #{email_id}
     , country_cd       = #{country_cd}
     , zip_code         = #{zip_code}
     , addr_1           = #{addr_1}
     , addr_2           = #{addr_2}
     , city_nm          = #{city_nm}
     , visit_pups_cd    = #{visit_pups_cd}
     , mod_user         = #{user_id}
     , mod_dtm          = NOW()
 WHERE cust_seq         = #{cust_seq}

/* 회원관심사항 삭제처리 */
UPDATE ev_cust_intrst
  SET del_yn    = 'Y'
    , mod_user  = #{user_id}
    , mod_dtm   =  NOW()
WHERE cust_id   = #{cust_id}

/* 회원비빌번호 변경 */
UPDATE ev_cust_mst
   SET cust_pw  = #{user_change_pw1}
     , mod_user = #{user_id}
     , mod_dtm  = NOW()
 WHERE cust_id  = #{cust_id}

/* 회원비빌번호 임시저장 */
UPDATE ev_cust_mst
SET cust_pw  = #{user_change_pw1}
  , mod_user = 'init_pw'
  , mod_dtm  = NOW()
WHERE cust_id  = #{cust_id}

/* 재동의 */
UPDATE ev_cust_mst
SET agree_1  = #{agree_1}
  , agree_dtm_1 = NOW()
  , agree_2  = #{agree_2}
  , agree_dtm_2 = NOW()
  , mod_user = #{user_id}
  , mod_dtm  = NOW()
WHERE cust_id  = #{cust_id}
