mapper.select:
/* 회원중복체크 */
SELECT COUNT(*) AS CNT
  FROM ev_cust_mst
 where cust_id = #{cust_id}

/* 추천인체크 */
SELECT COUNT(*) AS CNT
FROM ev_cust_mst
where cust_sts_cd = '105001'
and cust_clsf_cd = '202001'
and cust_id = #{cust_id}

/* 로그인 회원정보 조회 */
SELECT cust_seq
     , cust_id as cust_id
     , cust_id as user_id
     , cust_nm
     , email_id
     , cust_clsf_cd
     , date_format(ins_dtm,'%Y-%m-%d')  as ins_dt
FROM ev_cust_mst
where 1=1


            AND google_email = #{google_email}


            AND naver_email = #{naver_email}


            AND kakao_email = #{kakao_email}


            AND facebook_email = #{facebook_email}

/* 로그인 회원정보 조회 */
SELECT cust_seq
, cust_id as cust_id
, cust_id as user_id
, cust_nm
, email_id
, cust_clsf_cd
FROM ev_cust_mst
where cust_nm = #{cust_nm}
  and email_id = #{email_id}

/* 로그인 회원정보 조회 */
SELECT cust_seq
     , cust_id as cust_id
     , cust_id as user_id
     , cust_nm
     , email_id
     , cust_clsf_cd
FROM ev_cust_mst
where cust_id = #{cust_id}
  and cust_nm = #{cust_nm}
  and email_id = #{email_id}

mapper.insert:

    SELECT LAST_INSERT_ID() as cust_seq


/* 회원등록 */
INSERT INTO ev_cust_mst
(
      cust_seq, cust_id, cust_pw, cust_sts_cd, cust_clsf_cd, cust_nm
    , company_nm, dept_nm, rep_nm, posn_nm, gender_cd
    , age_cd, hp_no, tel_no, email_id, country_cd
    , zip_code, addr_1, addr_2
    , city_nm, visit_pups_cd, rcmder_cust_id, agree_1, agree_dtm_1
    , agree_2, agree_dtm_2, agree_3, agree_dtm_3, ins_user
    , ins_dtm, mod_user, mod_dtm, google_email, naver_email, kakao_email, facebook_email, reg_provider
    , category1, category2, category3
) VALUES (
      NULL, #{cust_id}, #{cust_pw}, #{cust_sts_cd}, #{cust_clsf_cd}, #{cust_nm}
    , #{company_nm}, #{dept_nm}, #{rep_nm}, #{posn_nm}, #{gender_cd}
    , #{age_cd}, #{hp_no}, #{tel_no}, #{email_id}, #{country_cd}
    , #{zip_code}, #{addr_1}, #{addr_2}
    , #{city_nm}, #{visit_pups_cd}, #{rcmder_cust_id}, #{agree_1}, NOW()
    , #{agree_2}, NOW(), #{agree_3}, NOW(), #{user_id}
    , NOW(), #{user_id}, NOW(), #{google_email}, #{naver_email}, #{kakao_email}, #{facebook_email}, #{reg_provider}
    , #{category1}, #{category2}, #{category3}
)

/* 회원관심사항 입력 */
INSERT INTO ev_cust_intrst
(
    intrst_id, cust_id, intrst_cd, del_yn, ins_user, ins_dtm, mod_user, mod_dtm
) VALUES (
    NULL, #{cust_id}, #{intrst_cd}, 'N', #{user_id}, NOW(), #{user_id}, NOW()
)

mapper.update:
/* 회원정보수정 */
UPDATE ev_cust_mst
   SET company_nm       = #{company_nm}
     , dept_nm          = #{dept_nm}
     , rep_nm           = #{rep_nm}
     , posn_nm          = #{posn_nm}
     , gender_cd        = #{gender_cd}
     , age_cd           = #{age_cd}
     , hp_no            = #{hp_no}
     , tel_no           = #{tel_no}
     , email_id         = #{email_id}
     , country_cd       = #{country_cd}
     , zip_code         = #{zip_code}
     , addr_1           = #{addr_1}
     , addr_2           = #{addr_2}
     , city_nm          = #{city_nm}
     , visit_pups_cd    = #{visit_pups_cd}
     , category1        = #{category1}
     , category2        = #{category2}
     , category3        = #{category3}
     , mod_user         = #{user_id}
     , mod_dtm          = NOW()
 WHERE cust_id         = #{cust_id}

/* 회원관심사항 삭제처리 */
UPDATE ev_cust_intrst
  SET del_yn    = 'Y'
    , mod_user  = #{user_id}
    , mod_dtm   =  NOW()
WHERE cust_id   = #{cust_id}

/* 회원비빌번호 변경 */
UPDATE ev_cust_mst
   SET cust_pw  = #{user_change_pw1}
     , mod_user = #{user_id}
     , mod_dtm  = NOW()
 WHERE cust_id  = #{cust_id}

/* 회원비빌번호 임시저장 */
UPDATE ev_cust_mst
SET cust_pw  = #{user_change_pw1}
  , mod_user = 'init_pw'
  , mod_dtm  = NOW()
WHERE cust_id  = #{cust_id}

/* 재동의 */
UPDATE ev_cust_mst
SET agree_1  = #{agree_1}
  , agree_dtm_1 = NOW()
  , agree_2  = #{agree_2}
  , agree_dtm_2 = NOW()
  , mod_user = #{user_id}
  , mod_dtm  = NOW()
WHERE cust_id  = #{cust_id}

mapper.select:
/* top 참여기업 조회 */ select a.* from ( select ecm.cust_id , ecm.cust_nm , ecm.category1 , nvl((select cd_nm from ev_comm_code ecc2 where ecc2.cd_id = ecm.category1),'') as category1_nm , ecm.category2 , ecm.category3 , nvl((select cd_val1 from ev_comm_code ecc where ecc.cd_id = ecm.category1),'#!') as vr_shortcut_url , (select case when count(*) = 0 then 'N' else 'Y' end as favorts_yn from ev_cust_favorts ecf where ecf.favorts_cust_id = ecm.cust_id and ecf.cust_id = #{cust_id} and del_yn = 'N') as favorts_yn , (select case when nvl(sum(badge_cnt),0) * (-1) = 0 then 'N' else 'Y' end as badge_yn from ev_cust_badge ecb where ecb.give_cust_id = ecm.cust_id and ecb.cust_id = #{cust_id} and badge_clsf_cd in ('211003', '211004') /* 사용, 회수 */) as badge_yn , (select nvl(sum(badge_cnt),0) * (-1) as badge_sum from ev_cust_badge ecb where ecb.give_cust_id = ecm.cust_id and badge_clsf_cd in ('211003', '211004') /* 사용, 회수 */) as badge_sum , (select file_path from ev_file_attach efa where efa.board_id = ecm.cust_seq and file_clsf_cd = '101008' and file_clsf_dtl_cd = '102006' and del_yn = 'N') as ci_url from ev_cust_mst ecm left outer join ev_cust_corp_info ecci on ecm.cust_id = ecci.cust_id where ecm.cust_sts_cd = '105001' and ecm.cust_clsf_cd = '202002'
and ecm.category1 = #{category}
and ecm.cust_nm like concat('%',#{keyword}, '%')
) a order by a.badge_sum desc, a.cust_nm asc
LIMIT #{page_row_start}, #{page_row_cnt}

/* top 참여기업 조회 */
select count(*) as row_count from ev_cust_mst ecm left outer join ev_cust_corp_info ecci on ecm.cust_id = ecci.cust_id where ecm.cust_sts_cd = '105001' and ecm.cust_clsf_cd = '202002'
and ecm.category1 = #{category}
and ecm.cust_nm like concat('%',#{keyword}, '%')

/* zoom 사용리스트 */ select zoom_mgnt_id , concat('zoom', zoom_mgnt_id) as zoom_id , cust_id , zoom_url , use_fr_dtm , use_to_dtm , use_yn from ev_zoom_use_mgnt ezum where del_yn = 'N'
and ezum.cust_id = #{cust_id}
order by cust_id, zoom_mgnt_id

mapper.update:
/* 상담예약-타임존 셋팅 */
