mapper.select:
select ROW_NUMBER() OVER(ORDER BY ecm.cust_nm, rsvecm.cust_nm) as rn , ecs.ev_expo_id , ecs.consult_time_id , ecm.cust_id as exhibitors_cust_id , rsvecm.cust_id as consult_rsv_cust_id , ecm.cust_nm as exhibitors_cust_nm , rsvecm.cust_nm as consult_rsv_cust_nm , sum(case when ecs.consult_rsv_stat_cd = '215001' then 1 else 0 end) as s215001_cnt , sum(case when ecs.consult_rsv_stat_cd = '215002' then 1 else 0 end) as s215002_cnt , sum(case when ecs.consult_rsv_stat_cd = '215003' then 1 else 0 end) as s215003_cnt , sum(case when ecs.consult_rsv_stat_cd = '215004' then 1 else 0 end) as s215004_cnt from ev_consult_schedule ecs , ev_consult_time ect , ev_cust_mst ecm , ev_cust_mst rsvecm where ecs.consult_time_id = ect.consult_time_id and ect.cust_id = ecm.cust_id and ecs.consult_rsv_cust_id = rsvecm.cust_id and ecs.ev_expo_id = #{ev_expo_id} and ecs.del_yn = 'N' and consult_rsv_cust_id is not null and consult_rsv_cust_id != ''
and ecm.cust_nm like concat('%',#{exhibitors_cust_nm},'%')
and rsvecm.cust_nm like concat('%',#{consult_rsv_cust_nm},'%')
and date_format(ecs.ins_dtm,'%Y-%m-%d') >= #{consult_dt_fr}
and date_format(ecs.ins_dtm,'%Y-%m-%d') <= #{consult_dt_to}
group by ecs.ev_expo_id, ecs.consult_time_id, ecm.cust_nm, rsvecm.cust_nm
LIMIT #{page_row_start}, #{page_row_cnt}

SELECT count(*) as row_count FROM ( select ecs.ev_expo_id, ecs.consult_time_id, ecm.cust_nm as exhibitors_cust_nm, rsvecm.cust_nm as consult_rsv_cust_nm from ev_consult_schedule ecs , ev_consult_time ect , ev_cust_mst ecm , ev_cust_mst rsvecm where ecs.consult_time_id = ect.consult_time_id and ect.cust_id = ecm.cust_id and ecs.consult_rsv_cust_id = rsvecm.cust_id and ecs.ev_expo_id = #{ev_expo_id} and ecs.del_yn = 'N' and consult_rsv_cust_id is not null and consult_rsv_cust_id != ''
and ecm.cust_nm like concat('%',#{exhibitors_cust_nm},'%')
and rsvecm.cust_nm like concat('%',#{consult_rsv_cust_nm},'%')
and date_format(ecs.ins_dtm,'%Y-%m-%d') >= #{consult_dt_fr}
and date_format(ecs.ins_dtm,'%Y-%m-%d') <= #{consult_dt_to}
group by ecs.ev_expo_id, ecs.consult_time_id, ecm.cust_nm, rsvecm.cust_nm ) a
