     , hp_no = #{hp_no}
     , use_yn = #{use_yn}
     , mod_user = #{user_id}
     , mod_dtm = now()
where mgnt_id = #{mgnt_id}

/* 관리자정보 수정 */
update ev_mgnt_user
set   mgnt_pw = #{mgnt_pw}
, mod_user = #{user_id}
, mod_dtm = now()
where mgnt_id = #{mgnt_id}

mapper.insert:
/* 관리자 메뉴 권한 */
INSERT INTO ev_menu_prms
(
    prms_id, mgnt_id, menu_id, use_yn, del_yn, ins_user, ins_dtm, mod_user, mod_dtm
) VALUES (
    NULL, #{mgnt_id}, #{menu_id}, #{use_yn}, 'N', #{user_id}, NOW(), #{user_id}, NOW()
)

mapper.update:
/* 권한삭제처리 */
update ev_menu_prms
set     del_yn = 'Y'
        , mod_user = #{user_id}
        , mod_dtm  = NOW()
where mgnt_id = #{mgnt_id}

mapper.select:
/* 메뉴권한조회 */
select menu_id
     , use_yn
  from ev_menu_prms
 where mgnt_id = #{mgnt_id}
  and del_yn = 'N'

with cust as ( select (select cd_nm from ev_comm_code ecc2 where cd_id = ecm.category1) as caregori_nm , ecm.cust_id , ecm.cust_nm from ev_cust_mst ecm where ecm.cust_clsf_cd = '202002' and ecm.cust_sts_cd = '105001' and exists (select 1 from ev_expo_customer where ev_expo_id = #{ev_expo_id} and cust_id = ecm.cust_id)
and ecm.cust_nm like concat('%',#{cust_nm},'%')
and ecm.category1 = #{category1}
and ecm.cust_id = #{cust_id}
) , access_log as ( /* 접속건수조회 */ select date_format(aml.access_dtm, '%Y-%m-%d') as access_dt , aml.ptcp_cust_id , sum(case when aml.access_menu_cd = '107001' then 1 else 0 end) as cd107001 /* 기업정보 */ , sum(case when aml.access_menu_cd = '107002' then 1 else 0 end) as cd107002 /* 자료소개 */ , sum(case when aml.access_menu_cd = '107003' then 1 else 0 end) as cd107003 /* 갤러리 */ , sum(case when aml.access_menu_cd = '107004' then 1 else 0 end) as cd107004 /* QA */ , sum(case when aml.access_menu_cd = '107005' then 1 else 0 end) as cd107005 /* 홍보동영상 */ , sum(case when aml.access_menu_cd = '107006' then 1 else 0 end) as cd107006 /* 링크바로가기 */ from access_menu_log aml where date_format(aml.access_dtm, '%Y-%m-%d') between #{ins_dt_fr} and #{ins_dt_to} group by date_format(aml.access_dtm, '%Y-%m-%d'), aml.ptcp_cust_id ) , access_dt as ( /* 접속일자조회 */ select date_format(access_dtm, '%Y-%m-%d') as dt from access_menu_log where date_format(access_dtm, '%Y-%m-%d') between #{ins_dt_fr} and #{ins_dt_to} group by date_format(access_dtm, '%Y-%m-%d') union select ins_dt from v_cust_favorts_badge where ins_dt between #{ins_dt_fr} and #{ins_dt_to} group by ins_dt ) select ROW_NUMBER() OVER(order by caregori_nm, cust_id, cust_nm, ins_dt desc) as rn , a.* from ( SELECT cust.caregori_nm, cust.cust_id, cust.cust_nm
, nvl(max(access_dt.dt),#{ins_dt_fr}) AS ins_dt
, nvl(access_dt.dt,#{ins_dt_fr}) AS ins_dt
, sum(case when fb.clsf_cd = 'F' then fb.cnt ELSE 0 END) AS favorts_cnt , sum(case when fb.clsf_cd = 'B' then fb.cnt ELSE 0 END) AS badge_cnt
, nvl((select sum(cd107001) from access_log where access_dt in (select access_dt.dt from access_dt) and ptcp_cust_id = cust.cust_id),0) AS access01_cnt /* 접속건수-기업정보 */ , nvl((select sum(cd107002) from access_log where access_dt in (select access_dt.dt from access_dt) and ptcp_cust_id = cust.cust_id),0) AS access02_cnt /* 접속건수-자료소개 */ , nvl((select sum(cd107003) from access_log where access_dt in (select access_dt.dt from access_dt) and ptcp_cust_id = cust.cust_id),0) AS access03_cnt /* 접속건수-갤러리 */ , nvl((select sum(cd107004) from access_log where access_dt in (select access_dt.dt from access_dt) and ptcp_cust_id = cust.cust_id),0) AS access04_cnt /* 접속건수-QA */ , nvl((select sum(cd107005) from access_log where access_dt in (select access_dt.dt from access_dt) and ptcp_cust_id = cust.cust_id),0) AS access05_cnt /* 접속건수-홍보동영상 */ , nvl((select sum(cd107006) from access_log where access_dt in (select access_dt.dt from access_dt) and ptcp_cust_id = cust.cust_id),0) AS access06_cnt /* 접속건수-링크바로가기 */
, nvl((select cd107001 from access_log where access_dt = access_dt.dt and ptcp_cust_id = cust.cust_id),0) AS access01_cnt /* 접속건수-기업정보 */ , nvl((select cd107002 from access_log where access_dt = access_dt.dt and ptcp_cust_id = cust.cust_id),0) AS access02_cnt /* 접속건수-자료소개 */ , nvl((select cd107003 from access_log where access_dt = access_dt.dt and ptcp_cust_id = cust.cust_id),0) AS access03_cnt /* 접속건수-갤러리 */ , nvl((select cd107004 from access_log where access_dt = access_dt.dt and ptcp_cust_id = cust.cust_id),0) AS access04_cnt /* 접속건수-QA */ , nvl((select cd107005 from access_log where access_dt = access_dt.dt and ptcp_cust_id = cust.cust_id),0) AS access05_cnt /* 접속건수-홍보동영상 */ , nvl((select cd107006 from access_log where access_dt = access_dt.dt and ptcp_cust_id = cust.cust_id),0) AS access06_cnt /* 접속건수-링크바로가기 */
from cust join access_dt left outer join v_cust_favorts_badge fb ON cust.cust_id = fb.cust_id and access_dt.dt = fb.ins_dt and access_dt.dt between #{ins_dt_fr} and #{ins_dt_to}
group BY cust.caregori_nm, cust.cust_id, cust.cust_nm
group BY cust.caregori_nm, cust.cust_id, cust.cust_nm, access_dt.dt
) a LIMIT #{page_row_start}, #{page_row_cnt}

with cust as ( select (select cd_nm from ev_comm_code ecc2 where cd_id = ecm.category1) as caregori_nm , ecm.cust_id , ecm.cust_nm from ev_cust_mst ecm where ecm.cust_clsf_cd = '202002' and ecm.cust_sts_cd = '105001' and exists (select 1 from ev_expo_customer where ev_expo_id = #{ev_expo_id} and cust_id = ecm.cust_id)
and ecm.cust_nm like concat('%',#{cust_nm},'%')
and ecm.category1 = #{category1}
and ecm.cust_id = #{cust_id}
) , access_log as ( /* 접속건수조회 */ select date_format(aml.access_dtm, '%Y-%m-%d') as access_dt , aml.ptcp_cust_id , sum(case when aml.access_menu_cd = '107001' then 1 else 0 end) as cd107001 /* 기업정보 */ , sum(case when aml.access_menu_cd = '107002' then 1 else 0 end) as cd107002 /* 자료소개 */ , sum(case when aml.access_menu_cd = '107003' then 1 else 0 end) as cd107003 /* 갤러리 */ , sum(case when aml.access_menu_cd = '107004' then 1 else 0 end) as cd107004 /* QA */ , sum(case when aml.access_menu_cd = '107005' then 1 else 0 end) as cd107005 /* 홍보동영상 */ , sum(case when aml.access_menu_cd = '107006' then 1 else 0 end) as cd107006 /* 링크바로가기 */ from access_menu_log aml where date_format(aml.access_dtm, '%Y-%m-%d') between #{ins_dt_fr} and #{ins_dt_to} group by date_format(aml.access_dtm, '%Y-%m-%d'), aml.ptcp_cust_id ) , access_dt as ( /* 접속일자조회 */ select date_format(access_dtm, '%Y-%m-%d') as dt from access_menu_log where date_format(access_dtm, '%Y-%m-%d') between #{ins_dt_fr} and #{ins_dt_to} group by date_format(access_dtm, '%Y-%m-%d') union select ins_dt from v_cust_favorts_badge where ins_dt between #{ins_dt_fr} and #{ins_dt_to} group by ins_dt ) select count(*) row_count from ( SELECT cust.caregori_nm, cust.cust_id, cust.cust_nm from cust join access_dt left outer join v_cust_favorts_badge fb ON cust.cust_id = fb.cust_id and access_dt.dt = fb.ins_dt and access_dt.dt between #{ins_dt_fr} and #{ins_dt_to}
group BY cust.caregori_nm, cust.cust_id, cust.cust_nm
group BY cust.caregori_nm, cust.cust_id, cust.cust_nm, access_dt.dt
) a

/* 접속로그 */ select ROW_NUMBER() OVER(order by ecll.login_dtm DESC) as rn , ecll.cust_id , ecm.cust_nm , ecll.ip , date_format(ecll.login_dtm , '%Y-%m-%d %H:%i:%s') as login_dtm from ev_cust_login_log ecll , ev_cust_mst ecm where ecll.cust_id = ecm.cust_id
and date_format(ecll.login_dtm,'%Y-%m-%d') BETWEEN #{ins_dt_fr} AND #{ins_dt_to}
and ecm.cust_nm like concat('%',#{access_nm},'%')
and ecm.cust_id like concat('%',#{access_id},'%')
LIMIT #{page_row_start}, #{page_row_cnt}

/* 접속로그 건수 */ select count(*) as row_count from ev_cust_login_log ecll , ev_cust_mst ecm where ecll.cust_id = ecm.cust_id
and date_format(ecll.login_dtm,'%Y-%m-%d') BETWEEN #{ins_dt_fr} AND #{ins_dt_to}
and ecm.cust_nm like concat('%',#{access_nm},'%')
and ecm.cust_id like concat('%',#{access_id},'%')

select ROW_NUMBER() OVER(order by expo_from_dt asc) as rn , ev_expo_id , ev_expo_nm , apct_prod_from_dt , apct_prod_to_dt , expo_from_dt , expo_from_hrm , concat(expo_from_dt, ' ', expo_from_hrm) as expo_from_dtm , expo_to_dt , expo_to_hrm , concat(expo_to_dt, ' ', expo_to_hrm) as expo_to_dtm , use_yn from ev_expo where 1 = 1
and ev_expo_nm like concat('%',#{ev_expo_nm},'%')
and expo_to_dt >= #{expo_dt_fr}
and expo_from_dt <= #{expo_dt_to}
and use_yn = #{use_yn}
LIMIT #{page_row_start}, #{page_row_cnt}

select count(*) as row_count
from ev_expo
where 1 = 1


    and ev_expo_nm like concat('%',#{ev_expo_nm},'%')



     and expo_from_dt <= #{expo_dt_fr} 



    and expo_to_dt   >= #{expo_dt_to}



    and use_yn   = #{use_yn}

mapper.insert:
INSERT INTO ev_expo (
      ev_expo_id,       ev_expo_nm,     apct_prod_from_dt,      apct_prod_to_dt,    expo_from_dt,       expo_from_hrm
    , expo_to_dt,       expo_to_hrm,    use_yn,                 ins_user,           ins_dtm,            mod_user,       mod_dtm
) VALUES(
      NULL,             #{ev_expo_nm},  #{apct_prod_from_dt},   #{apct_prod_to_dt}, #{expo_from_dt},    #{expo_from_hrm}
    , #{expo_to_dt},    #{expo_to_hrm}, #{use_yn},              #{user_id},         NOW(),              #{user_id},     NOW()
)

mapper.update:
UPDATE ev_expo
   SET ev_expo_nm           = #{ev_expo_nm}
     , apct_prod_from_dt    = #{apct_prod_from_dt}
     , apct_prod_to_dt      = #{apct_prod_to_dt}
     , expo_from_dt         = #{expo_from_dt}
     , expo_from_hrm        = #{expo_from_hrm}
     , expo_to_dt           = #{expo_to_dt}
     , expo_to_hrm          = #{expo_to_hrm}
     , use_yn               = #{use_yn}
     , mod_user             = #{user_id}
     , mod_dtm              = NOW()
WHERE ev_expo_id            = #{ev_expo_id}

mapper.delete:
INSERT INTO ev_expo_customer
(
    ev_expo_id, cust_id, ins_user, ins_dtm, mod_user, mod_dtm
) VALUES (
    #{ev_expo_id}, #{cust_id}, #{user_id}, NOW(), #{user_id}, NOW()
)

delete from ev_expo_customer
where ev_expo_id = #{ev_expo_id}
  and cust_id = #{cust_id}

mapper.select:
select ev_expo_id, ev_expo_nm, apct_prod_from_dt, apct_prod_to_dt, expo_from_dt, expo_from_hrm, expo_to_dt, expo_to_hrm, expo_consult_from_dt, expo_consult_to_dt, use_yn from ev_expo ee where 1 = 1
and use_yn = #{use_yn}
order by ee.expo_from_dt
