mapper.select:
/* 중복체크 */
select count(*) as dup_cnt
from ev_mgnt_user
where mgnt_id = #{mgnt_id}

mapper.insert:
/* 관리자등록 */
INSERT INTO ev_mgnt_user
(
    mgnt_seq, mgnt_id, mgnt_nm, mgnt_pw, email_id, hp_no, dept_nm, use_yn, del_yn, ins_user, ins_dtm, mod_user, mod_dtm
) VALUES (
    null, #{mgnt_id}, #{mgnt_nm}, #{mgnt_pw}, #{email_id}, #{hp_no}, #{dept_nm},'Y', 'N', #{user_id}, now(), #{user_id}, now()
)

mapper.update:
/* 관리자정보 수정 */
update ev_mgnt_user
set    mgnt_nm = #{mgnt_nm}
     , email_id = #{email_id}
     , dept_nm = #{dept_nm}
     , hp_no = #{hp_no}
     , use_yn = #{use_yn}
     , mod_user = #{user_id}
     , mod_dtm = now()
where mgnt_id = #{mgnt_id}

/* 관리자정보 수정 */
update ev_mgnt_user
set   mgnt_pw = #{mgnt_pw}
, mod_user = #{user_id}
, mod_dtm = now()
where mgnt_id = #{mgnt_id}

mapper.insert:
/* 관리자 메뉴 권한 */
INSERT INTO ev_menu_prms
(
    prms_id, mgnt_id, menu_id, use_yn, del_yn, ins_user, ins_dtm, mod_user, mod_dtm
) VALUES (
    NULL, #{mgnt_id}, #{menu_id}, #{use_yn}, 'N', #{user_id}, NOW(), #{user_id}, NOW()
)

mapper.update:
/* 권한삭제처리 */
update ev_menu_prms
set     del_yn = 'Y'
        , mod_user = #{user_id}
        , mod_dtm  = NOW()
where mgnt_id = #{mgnt_id}

mapper.select:
/* 메뉴권한조회 */
select menu_id
     , use_yn
  from ev_menu_prms
 where mgnt_id = #{mgnt_id}
  and del_yn = 'N'

with cust as ( select (select cd_nm from ev_comm_code ecc2 where cd_id = ecm.category1) as caregori_nm , ecm.cust_id , ecm.cust_nm from ev_cust_mst ecm where ecm.cust_clsf_cd = '202002' and ecm.cust_sts_cd = '105001'
and ecm.cust_nm like concat('%',#{cust_nm},'%')
and ecm.category1 = #{category1}
and ecm.cust_id = #{cust_id}
) SELECT ROW_NUMBER() OVER(order by cust.caregori_nm, cust.cust_id, cust.cust_nm, fb.ins_dt) as rn , cust.caregori_nm, cust.cust_id, cust.cust_nm, nvl(fb.ins_dt,#{ins_dt_fr}) AS ins_dt , sum(case when fb.clsf_cd = 'F' then fb.cnt ELSE 0 END) AS favorts_cnt , sum(case when fb.clsf_cd = 'B' then fb.cnt ELSE 0 END) AS badge_cnt , 0 AS access01_cnt /* 접속건수 예) 기업정보 */ , 0 AS access02_cnt /* 접속건수 */ , 0 AS access03_cnt /* 접속건수 */ , 0 AS access04_cnt /* 접속건수 */ , 0 AS access05_cnt /* 접속건수 */ , 0 AS access06_cnt /* 접속건수 */ , 0 AS access07_cnt /* 접속건수 */ , 0 AS access08_cnt /* 접속건수 */ , 0 AS access09_cnt /* 접속건수 */ , 0 AS access10_cnt /* 접속건수 */ , 0 AS access10_cnt /* 접속건수 */ , 0 AS access12_cnt /* 접속건수 */ , 0 AS access13_cnt /* 접속건수 */ , 0 AS access14_cnt /* 접속건수 */ , 0 AS access15_cnt /* 접속건수 */ FROM cust left outer join v_cust_favorts_badge fb ON cust.cust_id = fb.cust_id and fb.ins_dt between #{ins_dt_fr} and #{ins_dt_to} group BY cust.caregori_nm, cust.cust_id, cust.cust_nm, fb.ins_dt
LIMIT #{page_row_start}, #{page_row_cnt}

with cust as (
    select (select cd_nm from ev_comm_code ecc2 where cd_id = ecm.category1) as caregori_nm
        , ecm.cust_id
        , ecm.cust_nm
from ev_cust_mst ecm
where ecm.cust_clsf_cd = '202002'
and ecm.cust_sts_cd = '105001'


    and ecm.cust_nm like concat('%',#{cust_nm},'%')



    and ecm.category1 = #{category1}



    and ecm.cust_id = #{cust_id}


)
select count(*) row_count
  from (
        SELECT cust.caregori_nm, cust.cust_id, cust.cust_nm, fb.ins_dt
        FROM cust
        left outer join v_cust_favorts_badge fb
        ON cust.cust_id = fb.cust_id
        and fb.ins_dt between #{ins_dt_fr} and #{ins_dt_to}
        group BY cust.caregori_nm, cust.cust_id, cust.cust_nm, fb.ins_dt
    ) a
