mapper.update:
UPDATE ev_consult_time
SET consult_from_dt         = #{consult_from_dt}
, consult_to_dt         = #{consult_to_dt}
, tiemzone_cd           = #{tiemzone_cd}
, consult_from_time     = #{consult_from_time}
, consult_to_time       = #{consult_to_time}
, mod_user              = #{user_id}
, mod_dtm               = NOW()
WHERE consult_time_id = #{consult_time_id}

UPDATE ev_consult_time
SET tiemzone_cd      = #{tiemzone_cd}
, mod_user          = #{user_id}
, mod_dtm           = NOW()
WHERE consult_time_id = #{consult_time_id}

/* 상담스케줄 변경 시 삭제 처리 */
update ev_consult_schedule
set del_yn   = 'Y'
, mod_user = #{user_id}
, mod_dtm  = NOW()
where 1 = 1


    and schedule_id = #{schedule_id}


/* 고객 상담 스케줄 초기화 */

    and consult_time_id = #{consult_time_id}

mapper.select:
with cd as ( select nvl(max(cd_val1),'213001') as cd_val1 from ev_consult_time ect , ev_comm_code ecc where ev_expo_id = (select ev_expo_id from ev_expo where use_yn = 'Y') and ect.tiemzone_cd = ecc.cd_id
and ect.consult_time_id = #{consult_time_id}
and ect.consult_time_id = (select max(consult_time_id) from ev_consult_schedule where schedule_id = #{schedule_id})
) select ecs.schedule_id , ect.ev_expo_id , ect.consult_time_id , ect.cust_id /*, ecs.consult_dt*/ , ( date_format( date_add( str_to_date( concat(replace(ecs.consult_dt,'-',''), replace((select cd_nm from ev_comm_code where cd_id = ecs.consult_time_cd),':','')) , '%Y%m%d%H%i') ,interval nvl(cd.cd_val1,'0') hour) ,'%Y-%m-%d') ) as consult_dt , ecs.consult_time_cd , (select case when ect.tiemzone_cd = '213001' then cd_nm when ect.tiemzone_cd = '213002' then cd_val1 when ect.tiemzone_cd = '213003' then cd_val2 when ect.tiemzone_cd = '213004' then cd_val3 end from ev_comm_code ecc where cd_id = ecs.consult_time_cd) as consult_time_nm , ecs.consult_avail_yn , case when ecs.consult_avail_yn = 'N' then '' else ecs.consult_rsv_cust_id end as consult_rsv_cust_id , case when ecs.consult_avail_yn = 'N' then '' else ecm.cust_nm end as consult_rsv_cust_nm , case when ecs.consult_avail_yn = 'N' then '' else ecs.consult_rsv_stat_cd end as consult_rsv_stat_cd , case when ecs.consult_avail_yn = 'N' then '' else nvl(ecm.hp_no, ecm.tel_no) end as tel_no from ev_consult_schedule ecs left outer join ev_cust_mst ecm on ecs.consult_rsv_cust_id = ecm.cust_id , ev_consult_time ect , cd where ecs.consult_time_id = ect.consult_time_id
and ecs.schedule_id = #{schedule_id}
and ect.consult_time_id = #{consult_time_id} and ect.ev_expo_id = #{ev_expo_id} and (ecs.consult_avail_yn = 'Y' or ecs.consult_time_cd between ect.consult_from_time and ect.consult_to_time)
and nvl(ecs.consult_rsv_cust_id,'') in ('',#{consult_rsv_cust_id})
and nvl(ecs.consult_rsv_stat_cd,'') in ('','215001','215002') and ecs.del_yn = 'N' order by consult_time_nm asc

mapper.insert:
/* 상담메시지입력 */

    SELECT LAST_INSERT_ID() as consult_msg_id


INSERT INTO ev_consult_msg (
consult_msg_id, consult_time_id,    schedule_id,    snd_cust_id,
rcv_cust_id,    message,            rcv_yn,         ins_user,      ins_dtm
) VALUES (
NULL,           #{consult_time_id}, #{schedule_id}, #{snd_cust_id},
#{rcv_cust_id}, #{message},         'N',            #{cust_id},     NOW()
)

mapper.select:
select a.cust_id
, (select cust_nm from ev_cust_mst where cust_id = a.cust_id) as cust_nm
from (
select ecm.rcv_cust_id as cust_id
, ecm.ins_dtm
from ev_consult_msg ecm
where ecm.consult_time_id = #{consult_time_id}
and ecm.snd_cust_id  = #{cust_id}
and ecm.rcv_cust_id != #{cust_id}
union
select ecm.snd_cust_id as cust_id
, ecm.ins_dtm
from ev_consult_msg ecm
where ecm.consult_time_id = #{consult_time_id}
and ecm.rcv_cust_id  = #{cust_id}
and ecm.snd_cust_id != #{cust_id}
) a
group by a.cust_id
order by ins_dtm desc

/* 참관고객 상담시간 설정 */ with ptcpCust as ( select consult_time_id, ev_expo_id, cust_id, consult_from_dt, consult_to_dt, tiemzone_cd, consult_from_time, consult_to_time from ev_consult_time ect where cust_id = #{consultCustId} ) select a.ev_expo_id , a.cust_id , a.expo_from_dt , a.expo_to_dt , a.consult_from_time , a.consult_to_time , case when a.tiemzone_cd = '213001' then (select cd_nm from ev_comm_code ecc where cd_id = a.consult_from_time) when a.tiemzone_cd = '213002' then (select cd_val1 from ev_comm_code ecc where cd_id = a.consult_from_time) when a.tiemzone_cd = '213003' then (select cd_val2 from ev_comm_code ecc where cd_id = a.consult_from_time) when a.tiemzone_cd = '213004' then (select cd_val3 from ev_comm_code ecc where cd_id = a.consult_from_time) end as timezone_hhmm_fr , case when a.tiemzone_cd = '213001' then (select cd_nm from ev_comm_code ecc where cd_id = a.consult_to_time) when a.tiemzone_cd = '213002' then (select cd_val1 from ev_comm_code ecc where cd_id = a.consult_to_time) when a.tiemzone_cd = '213003' then (select cd_val2 from ev_comm_code ecc where cd_id = a.consult_to_time) when a.tiemzone_cd = '213004' then (select cd_val3 from ev_comm_code ecc where cd_id = a.consult_to_time) end as timezone_hhmm_to , a.tiemzone_cd from ( select ecs.ev_expo_id , nvl(ecs.consult_rsv_cust_id,#{cust_id}) as cust_id , max(eep.expo_from_dt) as expo_from_dt , max(eep.expo_to_dt) as expo_to_dt
, case when nvl(min(ecs.consult_time_cd),'214019') >= nvl(min(ptcpCust.consult_from_time),'214019') and nvl(min(ptcpCust.consult_from_time),'214019') < '214019' then nvl(min(ptcpCust.consult_from_time),'214019') when nvl(min(ecs.consult_time_cd),'214019') <= nvl(min(ptcpCust.consult_from_time),'214019') and nvl(min(ecs.consult_time_cd),'214019') < '214019' then nvl(min(ecs.consult_time_cd),'214019') else '214019' end as consult_from_time , case when nvl(max(ecs.consult_time_cd),'214036') >= nvl(max(ptcpCust.consult_to_time),'214036') and nvl(max(ecs.consult_time_cd),'214036') > '214036' then nvl(max(ecs.consult_time_cd),'214036') when nvl(max(ecs.consult_time_cd),'214036') <= nvl(max(ptcpCust.consult_to_time),'214036') and nvl(max(ptcpCust.consult_to_time),'214036') > '214036' then nvl(max(ptcpCust.consult_to_time),'214036') else '214036' end as consult_to_time
, (select nvl(tiemzone_cd,'213001') from ev_cust_mst where cust_id = #{cust_id}) as tiemzone_cd from ev_expo eep inner join ev_consult_schedule ecs on ecs.ev_expo_id = eep.ev_expo_id and ecs.consult_rsv_cust_id = #{cust_id} and ecs.del_yn = 'N' and ecs.consult_rsv_stat_cd in ('215001','215002') left outer join ptcpCust on eep.ev_expo_id = ptcpCust.ev_expo_id where eep.ev_expo_id = #{ev_expo_id} group by ecs.ev_expo_id, ecs.consult_rsv_cust_id ) a

/* 참관고객 채팅리스트 */
select a.cust_id
, (select cust_nm from ev_cust_mst where cust_id = a.cust_id) as cust_nm
from (

select ecm.rcv_cust_id as cust_id
, ecm.ins_dtm
from ev_consult_msg 	ecm
, ev_consult_time 	ect
where ecm.consult_time_id  = ect.consult_time_id
and ect.ev_expo_id 	= #{ev_expo_id}
and ecm.snd_cust_id  = #{cust_id}
and ecm.rcv_cust_id != #{cust_id}
union
select ecm.snd_cust_id as cust_id
, ecm.ins_dtm
from ev_consult_msg 	ecm
, ev_consult_time 	ect
where ecm.consult_time_id  = ect.consult_time_id
and ect.ev_expo_id   = #{ev_expo_id}
and ecm.rcv_cust_id  = #{cust_id}
and ecm.snd_cust_id != #{cust_id}

) a
group by a.cust_id
order by ins_dtm desc

select (
select count(*)
from ev_consult_msg 	ecm
, ev_consult_time 	ect
where ecm.consult_time_id    = ect.consult_time_id
and ect.ev_expo_id         = #{ev_expo_id}
and (snd_cust_id = #{cust_id} or rcv_cust_id = #{cust_id})
) as chat_cnt
, (
select count(*)
from ev_consult_schedule ecs
where ev_expo_id           = #{ev_expo_id}
and consult_rsv_cust_id  = #{cust_id}
and del_yn = 'N'
and consult_rsv_stat_cd in ('215001','215002')
) as consult_cnt
, (
select (count(*) * 30)/60
from ev_consult_schedule ecs
where ev_expo_id           = #{ev_expo_id}
and consult_rsv_cust_id  = #{cust_id}
and del_yn = 'N'
and consult_rsv_stat_cd in ('215001','215002')
) as consult_time

select schedule_id, ev_expo_id, consult_time_id, consult_dt, consult_time_cd, consult_rsv_cust_id, consult_rsv_stat_cd, consult_avail_yn, del_yn
from ev_consult_schedule ecs
where schedule_id = #{schedule_id}

/* 참가자 상담현황 */ with consult_list as ( select ROW_NUMBER() OVER (ORDER by ecs.consult_dt asc, ecs.consult_time_cd, ecs.consult_rsv_cust_id) as rn , ecs.consult_rsv_cust_id , (select cust_nm from ev_cust_mst ecm where cust_id = ecs.consult_rsv_cust_id) as consult_rsv_cust_nm , concat(ecs.consult_dt,' ', (select cd_nm from ev_comm_code ecc where cd_id = ecs.consult_time_cd)) as consult_dt_dtm , ect.tiemzone_cd , (select cd_val1 from ev_comm_code ecc2 where cd_id = ect.tiemzone_cd) as tiemzone_val , ecs.consult_rsv_stat_cd , (select cd_nm from ev_comm_code ecc2 where cd_id = ecs.consult_rsv_stat_cd) as consult_rsv_stat_nm , ecs.mod_dtm , ecs.schedule_id from ev_consult_time ect inner join ev_consult_schedule ecs on ect.consult_time_id = ecs.consult_time_id and ecs.consult_rsv_cust_id is not null and ecs.consult_rsv_cust_id != '' and ecs.del_yn = 'N' where ect.ev_expo_id = #{ev_expo_id} and ect.cust_id = #{cust_id}
and ecs.consult_dt >= #{sh_consult_from_dt}
and ecs.consult_dt <= #{sh_consult_to_dt}
and ecs.consult_rsv_stat_cd = #{sh_consult_sts_cd}
LIMIT #{page_row_start}, #{page_row_cnt} ) select list.rn , list.consult_rsv_cust_id , list.consult_rsv_cust_nm , date_format(date_add(str_to_date(replace(replace(replace(list.consult_dt_dtm,'-',''),' ',''),':',''), '%Y%m%d%H%i'),interval nvl(list.tiemzone_val,'0') hour),'%Y-%m-%d %H:%i') as consult_dt_dtm , list.tiemzone_cd , list.tiemzone_val , list.consult_rsv_stat_cd , list.consult_rsv_stat_nm , date_format(list.mod_dtm, '%Y-%m-%d %H:%i') as mod_dtm , list.schedule_id from consult_list list

/* 참가자 상담현황 */
select  COUNT(*) AS row_count
from ev_consult_time ect
inner join ev_consult_schedule ecs
on ect.consult_time_id  = ecs.consult_time_id
and ecs.consult_rsv_cust_id is not null
and ecs.consult_rsv_cust_id != ''
and ecs.del_yn = 'N'
where ect.ev_expo_id = #{ev_expo_id}
and ect.cust_id = #{cust_id}


    and ecs.consult_dt >= #{sh_consult_from_dt}



    and ecs.consult_dt <= #{sh_consult_to_dt}



    and ecs.consult_rsv_stat_cd = #{sh_consult_sts_cd}

/* 참관자 상담현황 */ with consult_list as ( select ROW_NUMBER() OVER (ORDER by ecs.consult_dt asc, ecs.consult_time_cd, ecs.consult_rsv_cust_id) as rn , ecs.consult_rsv_cust_id , (select cust_nm from ev_cust_mst ecm where cust_id = ect.cust_id) as consult_rsv_cust_nm , concat(ecs.consult_dt,' ', (select cd_nm from ev_comm_code ecc where cd_id = ecs.consult_time_cd)) as consult_dt_dtm , ecm.tiemzone_cd , (select cd_val1 from ev_comm_code ecc2 where cd_id = ecm.tiemzone_cd) as tiemzone_val , ecs.consult_rsv_stat_cd , (select cd_nm cd_nm_en from ev_comm_code ecc2 where cd_id = ecs.consult_rsv_stat_cd) as consult_rsv_stat_nm , ecs.mod_dtm , ect.consult_time_id , ecs.schedule_id from ev_consult_time ect inner join ev_consult_schedule ecs on ect.consult_time_id = ecs.consult_time_id and ecs.consult_rsv_cust_id = #{cust_id} and ecs.del_yn = 'N' inner join ev_cust_mst ecm on ecs.consult_rsv_cust_id = ecm.cust_id where ect.ev_expo_id = #{ev_expo_id}
and ecs.consult_dt >= #{sh_consult_from_dt}
and ecs.consult_dt <= #{sh_consult_to_dt}
and ecs.consult_rsv_stat_cd = #{sh_consult_sts_cd}
LIMIT #{page_row_start}, #{page_row_cnt} ) select list.rn , list.consult_rsv_cust_id , list.consult_rsv_cust_nm , date_format(date_add(str_to_date(replace(replace(replace(list.consult_dt_dtm,'-',''),' ',''),':',''), '%Y%m%d%H%i'),interval nvl(list.tiemzone_val,'0') hour),'%Y-%m-%d %H:%i') as consult_dt_dtm , list.tiemzone_cd , list.tiemzone_val , list.consult_rsv_stat_cd , list.consult_rsv_stat_nm , date_format(list.mod_dtm, '%Y-%m-%d %H:%i') as mod_dtm , list.consult_time_id , list.schedule_id from consult_list list

/* 참관 상담현황 */
select  COUNT(*) AS row_count
from ev_consult_time ect
inner join ev_consult_schedule ecs
on ect.consult_time_id  = ecs.consult_time_id
and ecs.consult_rsv_cust_id = #{cust_id}
and ecs.del_yn = 'N'
inner join ev_cust_mst ecm
on ecs.consult_rsv_cust_id = ecm.cust_id
where ect.ev_expo_id = #{ev_expo_id}


    and ecs.consult_dt >= #{sh_consult_from_dt}



    and ecs.consult_dt <= #{sh_consult_to_dt}



    and ecs.consult_rsv_stat_cd = #{sh_consult_sts_cd}
